import React from "react";

const Tarea = ({ tarea }) => {
    const [nombre, estado] = tarea;
    return (
        <div className="tarea sombra">
            <p>{nombre}</p>

            <div className="estado">
                {estado ? (
                    <button type="button" className="completo">
                        Completo
                    </button>
                ) : (
                    <button type="button" className="incompleto">
                        Incompleto
                    </button>
                )}
            </div>
        </div>
    );
};

export default Tarea;
