import React, {useReducer} from 'react';

import TareasContext from "./tareasContext";
import TareasReducer from "./tarasReducer";
import clienteAxios from "../../config/axios";
import {
    OBTERNER_TAREAS,
    AGREGAR_TAREAS,
    ERROR_TAREAS,
    ELIMINAR_TAREA,
    ESTADO_TAREA,
    TAREA_ACTUAL,
    ACTUALIZAR_TAREA
} from "../../type/index"


const TareasState = props => {

    const initialState = {

        tareasProyecto: [],
        errorFormularioTarea: null,
        tareaSeleccionada: null

    }

    const [state, dispatch] = useReducer(TareasReducer, initialState)

    //series de funciones
    const obtenerTareas = proyectoId => {
        dispatch({
            type: OBTERNER_TAREAS,
            payload: proyectoId
        })
    }

    const agregarTareas = async tarea => {
        try {
            const respuesta = await clienteAxios.post('api/tareas', tarea);

            console.log(respuesta);

            dispatch({
                type: AGREGAR_TAREAS,
                payload: tarea,
            })
        } catch (error) {
            console.log(error);

        }
    }

    const errorTarea = valor => {
        dispatch({
            type: ERROR_TAREAS,
            payload: valor
        })
    }

    const elimiarTarea = id => {
        dispatch({
            type: ELIMINAR_TAREA,
            payload: id
        })
    }

    const estadoTarea = tarea => {
        dispatch({
            type: ESTADO_TAREA,
            payload: tarea
        })
    }

    const tareaActual = tarea => {
        dispatch({
            type: TAREA_ACTUAL,
            payload: tarea
        })
    }

    const actualizarTarea = tarea => {
        dispatch({
            type: ACTUALIZAR_TAREA,
            payload: tarea
        })
    }

    return (
        <TareasContext.Provider
        value= {{
            tareas: state.tareas,
            tareasProyecto: state.tareasProyecto,
            errorFormularioTarea: state.errorFormularioTarea,
            tareaSeleccionada: state.tareaSeleccionada,

            obtenerTareas,
            agregarTareas,
            errorTarea,
            elimiarTarea,
            estadoTarea,
            tareaActual,
            actualizarTarea,
        }}
        >
            {props.children}
        </TareasContext.Provider>
    )
}

export default TareasState;
