import React, {Fragment, useContext} from 'react'
import Tarea from "./Tarea"
import proyectoContext from "../../context/proyectos/proyectoContext";

const ListadoTarea = () => {

    const proyectosContext = useContext(proyectoContext)
    const {proyecto} = proyectosContext;

    if(!proyecto) return <h2>Selecciona Proyecto</h2>

    const [proyectoActual, eliminarProyecto] = proyecto;

    //tareas de prueba
    const tareas = [
        {nombre: "Hacer vista", estado: true},
        {nombre: "Hacer vista2", estado: false},
        {nombre: "Hacer vista3", estado: true},
        {nombre: "Hacer vista4", estado: false},
    ]

    const onClickElimina = () => {
        eliminarProyecto(proyectoActual.id)
    }
    return (
        <Fragment>
            <h2>Proyecto: {proyectoActual.nombre}</h2>

           <ul>
           <div className="listado-tareas">
                {tareas.length === 0
                    ? <li className="tarea"><p>No hay tareas</p></li>

                    : tareas.map(tarea => (
                        <Tarea 
                            tarea={tarea}
                        />
                    ))
                }
            </div>
           </ul>

           <button
            type="button"
            className="btn btn-eliminar"
            onClick={onClickElimina}
            >Eliminar Proyecto &times;</button>
        </Fragment>
    );
}

export default ListadoTarea;
