import {OBTERNER_TAREAS} from "../../type/index";

export default (state, action) => {

    switch (action.type) {

    case OBTERNER_TAREAS:
        return {
            ...state,
            tareasProyecto: state.tareas.filter(tarea => tarea.proyectoId === action.payload )
        }
        default:
            return state;
    }
}
