import React, {useReducer} from 'react';

import TareasContext from "./tareasContext";
import TareasReducer from "./tarasReducer";
import {
    OBTERNER_TAREAS,
    AGREGAR_TAREAS,
    ERROR_TAREAS,
    ELIMINAR_TAREA,
    ESTADO_TAREA,
    TAREA_ACTUAL,
    ACTUALIZAR_TAREA
} from "../../type/index"


const TareasState = props => {

    const initialState = {
        tareas: [
            {id: 1,nombre: "Hacer vista", estado: true, proyectoId: 1},
            {id: 2,nombre: "Hacer vista2", estado: false, proyectoId: 2},
            {id: 3,nombre: "Hacer vista3", estado: true, proyectoId: 3},
            {id: 4,nombre: "Hacer vista4", estado: false, proyectoId: 4},
            {id: 5,nombre: "Hacer vista2", estado: false, proyectoId: 3},
            {id: 6,nombre: "Hacer vista3", estado: true, proyectoId: 3},
            {id: 7,nombre: "Hacer vista4", estado: false, proyectoId: 4},
            {id: 8,nombre: "Hacer vista2", estado: false, proyectoId: 2},
            {id: 9,nombre: "Hacer vista3", estado: true, proyectoId: 2},
            {id: 10,nombre: "Hacer vista4", estado: false, proyectoId: 4},
            {id: 11,nombre: "Hacer vista2", estado: false, proyectoId: 1},
            {id: 12,nombre: "Hacer vista3", estado: true, proyectoId: 1},
            {id: 13,nombre: "Hacer vista4", estado: false, proyectoId: 4},
        ],
        tareasProyecto: null,
        errorFormularioTarea: null,
        tareaSeleccionada: null

    }

    const [state, dispatch] = useReducer(TareasReducer, initialState)

    //series de funciones
    const obtenerTareas = proyectoId => {
        dispatch({
            type: OBTERNER_TAREAS,
            payload: proyectoId
        })
    }

    const agregarTareas = tarea => {
        dispatch({
            type: AGREGAR_TAREAS,
            payload: tarea,
        })
    }

    const errorTarea = valor => {
        dispatch({
            type: ERROR_TAREAS,
            payload: valor
        })
    }

    const elimiarTarea = id => {
        dispatch({
            type: ELIMINAR_TAREA,
            payload: id
        })
    }

    const estadoTarea = tarea => {
        dispatch({
            type: ESTADO_TAREA,
            payload: tarea
        })
    }

    const tareaActual = tarea => {
        dispatch({
            type: TAREA_ACTUAL,
            payload: tarea
        })
    }

    const actualizarTarea = tarea => {
        dispatch({
            type: ACTUALIZAR_TAREA,
            payload: tarea
        })
    }

    return (
        <TareasContext.Provider
        value= {{
            tareas: state.tareas,
            tareasProyecto: state.tareasProyecto,
            errorFormularioTarea: state.errorFormularioTarea,
            tareaSeleccionada: state.tareaSeleccionada,

            obtenerTareas,
            agregarTareas,
            errorTarea,
            elimiarTarea,
            estadoTarea,
            tareaActual,
            actualizarTarea,
        }}
        >
            {props.children}
        </TareasContext.Provider>
    )
}

export default TareasState;
