import React, { useState, useContext } from "react";
import { Link } from "react-router-dom";
import alertasContex from "../../context/alertas/alertasContex"

const NuevaCuenta = () => {

    const alertaContext = useContext(alertasContex)

    const {alerta, mostrarAlerta } = alertaContext;

    //state de datos del usuario
    const [DatosUsuario, setDatosUsuario] = useState({
        nombre: "",
        email: "",
        password: "",
        confirmar: "",
    });

        //sacamos los datos del usuario
        const {nombre, email, password, confirmar } = DatosUsuario;

    const onChange = (e) => {
        setDatosUsuario({
            ...DatosUsuario,
            [e.target.name]: e.target.value,
        });
    };

    const onSubmit = (e) => {
        e.preventDefault();

        //validar
        if(nombre.trim() === '' || email.trim() === '' || password.trim() === '' || confirmar.trim() === '' ) {
            mostrarAlerta("Todos los datos son Obligatorios", "error")
        }
        //pasarlo al action
    };


    return (
        <div className="form-usuario ">
            {alerta ? <div className={`alerta ${alerta.categoria}`}>{alerta.msg}</div> : null}
            <div className="contenedor-form sombra-dark">
                <h1>Obtener Cuenta</h1>

                <form onSubmit={onSubmit}>
                    <div className="campo-form">
                        <label htmlFor="email">Nombre</label>
                        <input
                            type="text"
                            name="nombre"
                            placeholder="Tu Nombre"
                            value={nombre}
                            onChange={onChange}
                        />
                    </div>
                    <div className="campo-form">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            name="email"
                            placeholder="Tu Email"
                            value={email}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            name="password"
                            placeholder="Tu Password"
                            value={password}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="password">Confirmar Password</label>
                        <input
                            type="password"
                            name="confirmar"
                            placeholder="Repite tu password"
                            value={confirmar}
                            onChange={onChange}
                        />
                    </div>

                    <div className="campo-form">
                        <input
                            type="submit"
                            className="btn btn-primario btn-block"
                            value="Iniciar Sesion"
                        />
                    </div>
                </form>

                <Link to={"/"} class="enlace-cuenta">
                    Volver a inicio de Sesion
                </Link>
            </div>
        </div>
    );
};

export default NuevaCuenta;
