import React, {useReducer} from 'react';

import TareasContext from "./tareasContext";
import TareasReducer from "./tarasReducer";
import {OBTERNER_TAREAS} from "../../type/index"


const TareasState = props => {

    const initialState = {
        tareas: [
            {nombre: "Hacer vista", estado: true, proyectoId: 1},
            {nombre: "Hacer vista2", estado: false, proyectoId: 2},
            {nombre: "Hacer vista3", estado: true, proyectoId: 3},
            {nombre: "Hacer vista4", estado: false, proyectoId: 4},
            {nombre: "Hacer vista2", estado: false, proyectoId: 3},
            {nombre: "Hacer vista3", estado: true, proyectoId: 3},
            {nombre: "Hacer vista4", estado: false, proyectoId: 4},
            {nombre: "Hacer vista2", estado: false, proyectoId: 2},
            {nombre: "Hacer vista3", estado: true, proyectoId: 2},
            {nombre: "Hacer vista4", estado: false, proyectoId: 4},
            {nombre: "Hacer vista2", estado: false, proyectoId: 1},
            {nombre: "Hacer vista3", estado: true, proyectoId: 1},
            {nombre: "Hacer vista4", estado: false, proyectoId: 4},
        ],
        tareasProyecto: null,

    }

    const [state, dispatch] = useReducer(TareasReducer, initialState)

    //series de funciones
    const obtenerTareas = proyectoId => {
        dispatch({
            type: OBTERNER_TAREAS,
            payload: proyectoId
        })
    }

    const agregarTareas = tarea => {
        dispatch({
            ...state,
            payload: tarea,
        })
    }

    return (
        <TareasContext.Provider
        value= {{
            tareas: state.tareas,
            tareasProyecto: state.tareasProyecto,

            obtenerTareas,
        }}
        >
            {props.children}
        </TareasContext.Provider>
    )
}

export default TareasState;
