import {
    FORMULARIO_PROYECTO,
    OBTENER_PROYECTO,
    AGREGAR_PROYECTO,
    ERROR_FORMULARIO,
    PROYECTO_ACTUAL,
} from "../../type/index";

//aqui es donde cambian los states del proyecto
export default (state, action) => {
    switch (action.type) {
        case FORMULARIO_PROYECTO:
            return {
                ...state,
                formulario: true,
            };
        case OBTENER_PROYECTO:
            return {
                ...state,
                proyectos: action.payload,
            };
        case AGREGAR_PROYECTO:
            return {
                ...state,
                proyectos: [...state.proyectos, action.payload],
                formulario: false,
                error: false,
            };
        case  ERROR_FORMULARIO: 
            return {
                ...state,
                error: true
            }
        case PROYECTO_ACTUAL:
            return {
                ...state,
                proyecto: state.proyectos.filter( proyecto => proyecto.id === action.payload)
            } 

        default:
            return state;
    }
};
