import React, {useState} from 'react'
import {Link} from "react-router-dom"

const NuevaCuenta = () => {
        //state de datos del usuario
    const [DatosUsuario, setDatosUsuario] = useState({
        nombre:'',
        email:'',
        password:'',
        confirmar:''
    });

    const onChange = e => {
        setDatosUsuario({
            ...DatosUsuario,
            [e.target.name] : e.target.value
        })
    }

    const onSubmit = e => {
        e.preventDefault()

        //validar 

        //pasarlo al action


    }

    const {nombre, email, password, confirmar} = DatosUsuario;


    return (
        <div className="form-usuario ">
            <div className="contenedor-form sombra-dark">
                <h1>Obtener Cuenta</h1>

                <form
                    onSubmit={onSubmit}
                >
                                        <div className="campo-form">
                        <label htmlFor="email">Nombre</label>
                        <input 
                        type="text"
                        name="email"
                        placeholder="Tu Nombre"
                        value={nombre}
                        onChange={onChange}
                    />
                    </div>
                    <div className="campo-form">
                        <label htmlFor="email">Email</label>
                        <input 
                        type="email"
                        name="email"
                        placeholder="Tu Email"
                        value={email}
                        onChange={onChange}
                    />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="password">Password</label>
                        <input 
                        type="password"
                        name="password"
                        placeholder="Tu Password"
                        value={password}
                        onChange={onChange}
                    />
                    </div>

                    <div className="campo-form">
                        <label htmlFor="password">Confirmar Password</label>
                        <input 
                        type="password"
                        name="confirmar"
                        placeholder="Repite tu password"
                        value={confirmar}
                        onChange={onChange}
                    />
                    </div>

                    <div className="campo-form">
                        <input type="submit" className="btn btn-primario btn-block" value="Iniciar Sesion"/>
                    </div>
                </form>

                <Link to={"/nueva-cuenta"} class="enlace-cuenta" >Obtener Cuenta</Link>
            </div>
        </div>
    )
}

export default NuevaCuenta;
