import {MOSTRAR_ALERTA, OCULTAR_ALERTA} from "../../type"

export default (state, action) => {
    switch (action.type) {
        case MOSTRAR_ALERTA:
            return {
                alerta: action.payload
            }

        case OCULTAR_ALERTA :
            return {
                alerta: null
            }

        default:
            break;
    }
}
