import {
    OBTERNER_TAREAS,
    AGREGAR_TAREAS,
    ERROR_TAREAS,
} from "../../type/index";

export default (state, action) => {

    switch (action.type) {

    case OBTERNER_TAREAS:
        return {
            ...state,
            tareasProyecto: state.tareas.filter(tarea => tarea.proyectoId === action.payload )
        }

    case AGREGAR_TAREAS:
        return {
            ...state,
            tareas: [...state.tareas, action.payload]

        }

    case ERROR_TAREAS: 
        return {
            ...state,
            errorTarea: action.payload
        }

        default:
            return state;
    }
}
